import React, { useState } from 'react';

const App = () => {
  const state = useState();

  const [count, setCount] = useState(20);

  const click = () => {
    setCount(100);
  }
  return (
    <>
      <h1>{click}</h1>
      <button>Click Here</button>
    </>
  )
};

export default App
