import React, { useState } from 'react';

const App = () => {
  const count = useState(10);
  const click = () => {
    count ++;
    return console.log("clicked")
  }
  return (
    <>
      <h1>{count}</h1>
      <button onClick={click}>Click Here</button>
    </>
  )
};

export default App
