import React from 'react'
import { useState } from 'react/cjs/react.development';

const App = () => {
  const state = useState();
  const [count,updatedValue] = useState(0);
  const incre = () => {
    count ++;
  }
  return(
    <>
    <h1>{count}</h1>
    <button onClick={incre}>Click Here</button>
    </>
  )
};

export default App;
